    /**
     * Customize general Spark options.
     *
     * @return void
     */
    protected function customizeSpark()
    {
        Spark::configure([
            'models' => [
                'teams' => Team::class,
            ]
        ]);

        // Make sure users can make teams
        Spark::validateNewTeamsWith(function() {
            return (new TeamValidation())->validateNewTeamsWith();
        });
    }

