    /**
     * Customize the subscription plans for the application.
     *
     * @return void
     */
    protected function customizeSubscriptionPlans()
    {
        // Monthly plans
        Spark::free('Individual')
            ->features([
                'Web forms',
                '5 events per month',
                'Unlimited sign ups',
                'Salesforce integration',
            ]);

        Spark::plan('Basic', config('services.stripe.plans.basic_monthly_plan_id'))->price(100)
            ->features([
                'Android, iOS, and Web forms',
                'Unlimited events',
                'Unlimited sign ups',
                'Salesforce integration',
                'Single team',
            ]);

        Spark::plan('Premium', 'todo-stripe-plan-id')->price(500)
            ->features([
                'Android, iOS, and Web forms',
                'Unlimited events',
                'Unlimited sign ups',
                'Salesforce integration',
                'Multiple teams',
                'Theme customization',
            ]);

        // Yearly plans
        Spark::plan('Basic', 'todo-stripe-plan-id')->price(12 * 80)
            ->yearly()
            ->features([
                'Android, iOS, and Web forms',
                'Unlimited events',
                'Unlimited sign ups',
                'Salesforce integration',
                'Single department',
            ]);

        Spark::plan('Premium', 'todo-stripe-plan-id')->price(12 * 400)
            ->yearly()
            ->features([
                'Android, iOS, and Web forms',
                'Unlimited events',
                'Unlimited sign ups',
                'Salesforce integration',
                'Multiple departments',
                'Theme customization',
            ]);
    }
